mapper.select:
select  from t_auth_user

	
		and login_name = #{loginName, jdbcType=VARCHAR}
	
	
		and password = #{password, jdbcType=VARCHAR}
	
	
		and status = #{status, jdbcType=INTEGER}
	
	
		and type = #{type, jdbcType=INTEGER}
	
	
		and birthday = #{birthday, jdbcType=DATE}
	
	
		and payment_code = #{paymentCode, jdbcType=VARCHAR}
	
	
		and create_time = #{createTime, jdbcType=TIMESTAMP},
	
	
		and user_no = #{userNo, jdbcType=BIGINT}

SELECT u.id, u.login_name, u.status, u.type, u.birthday, u.payment_code, u.create_time, u.user_no, r.id role_id, r.name role_name FROM (
) u left JOIN t_auth_user_role ur ON u.ID = ur.USER_ID left JOIN t_auth_role r ON ur.ROLE_ID = r.ID
order by ${sort}
${order}

mapper.sql:
SELECT * from t_auth_user

	
		and login_name like concat('%', #{loginName, jdbcType=VARCHAR}, '%')
	
	
		and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s')  >=  #{createTimeStart}
	
	
		and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s')  <=  #{createTimeEnd}
	

LIMIT #{pageModel.start, jdbcType=INTEGER}, #{pageModel.pageShow, jdbcType=INTEGER}

